    public string GenerateLoremIpsum(int charCount, bool isRandom)
    {
        if (charCount <= 0)
            return "";

        string result = "";
        int loremLength = loremIpsum.Length;

        int startIndex = isRandom
            ? GetRandomStartIndex()
            : 0;

        while (result.Length < charCount)
        {
            int remainingChars = charCount - result.Length;

            if (remainingChars >= (loremLength - startIndex))
            {
                result += loremIpsum.Substring(startIndex);
                startIndex = 0;
            }
            else
            {
                result += loremIpsum.Substring(startIndex, remainingChars);
                break;
            }
        }

        return result;

        int GetRandomStartIndex()
        {
            int index;
            do
            {
                index = Random.Range(0, loremLength);
            }
            while (!char.IsLetter(loremIpsum[index]));
            return index;
        }
    }
